import React, { useEffect } from 'react';
import './index.css';
// import {isValidMove} from './script.js'
// import io from 'socket.io-client'


const App = () => {
  useEffect(() => {

    // this is board look initially
    const boardlook = [
      ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
      ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
      [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
      [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
      [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
      [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
      ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
      ['R', 'N', 'B', 'K', 'Q', 'B', 'N', 'R'],
    ];
    let chessboard = document.getElementById('chessboard')
    let i, j
    let diffId = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'] //used to create unique id for the image id


    // this below loop is used to add the chessbox inot the board
    for (i = 0; i < 8; i++) {
      for (j = 0; j < 8; j++) {
        let box = document.createElement('div')
        let color = (i + j) % 2 === 0 ? 'white' : 'black';
        let uniqueid = diffId[j] + i;
        box.innerText = boardlook[i][j]
        box.id = uniqueid
        box.classList.add(color, 'boxpices')
        //creating image to append into box
        let image = document.createElement('img')
        if (box.innerText !== ' ') {
          let src = box.innerText
          box.innerText = ' '
          image.src = `${src}`
          image.classList.add('pices')
          box.appendChild(image)
        }
        chessboard.appendChild(box)
      }
    }
  })

  return (
    <div>
      <div id='chessboard' >

      </div>
    </div>
  );
};

export default App;
