import React, { useEffect, useState } from 'react';
import './index.css';
import io from 'socket.io-client'
import { isValidMove } from './logic.js'
import { picesImageswhiteblack } from './pices/exportimages.js'

const App = () => {
  const [Piece, setPiece] = useState('')
  const [Start, setStart] = useState('')
  const [End, setEnd] = useState('')
  const boardlook = [
    ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
    ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
    ['R', 'N', 'B', 'K', 'Q', 'B', 'N', 'R'],
  ];
  useEffect(() => {

    // this is board look initially

    let chessboard = document.getElementById('chessboard')
    let i, j

    // this below loop is used to add the chessbox into the board
    for (i = 0; i < 8; i++) {
      for (j = 0; j < 8; j++) {
        let box = document.createElement('div')
        let color = (i + j) % 2 === 0 ? 'white' : 'black';
        box.innerText = boardlook[i][j]
        box.id = `${j}` + `${i}`
        box.classList.add(color, 'boxpices')
        //creating image to append into box
        let image = document.createElement('img')
        if (box.innerText !== ' ') {
          let src = box.innerText
          box.innerText = ' '
          image.src = picesImageswhiteblack[src]
          image.id = src
          image.classList.add('pices')
          box.appendChild(image)
        }
        chessboard.appendChild(box)
      }
    }
    // socket.io connection for fetching moves


  })

  // this below is used to handle dragging element and append that piece into this
  useEffect(() => {
    const chessSquares = document.querySelectorAll('.boxpices');
    const allPices = document.querySelectorAll('.pices');
    allPices.forEach(pice => {
      pice.addEventListener('dragstart', (e) => {
        pice.classList.add('dragable');
        setStart(pice.parentNode.id)
      });
      pice.addEventListener('dragend', () => {
        setEnd(pice.parentNode.id)
        setPiece(pice.id)
        pice.classList.remove('dragable');
      });
    });

    // initilasing socket for sending positions of the pices


    chessSquares.forEach(square => {
      square.addEventListener('dragover', (e) => {
        e.preventDefault();
        const draggingElement = document.querySelector('.dragable');
        if (draggingElement) {
          square.appendChild(draggingElement);
        }
      });
    });

    // sending the moves to verify that the move is valid or not

    if (Start !== '' && End !== '' && Piece !== '') {
      let from = Start.split('').map((e) => Number(e));
      let to = End.split('').map((e) => Number(e))
      let moveresult = isValidMove(from,to,Piece)

        let socket = io('http://localhost:8010')
        socket.emit('room1',from,to,Piece)
        socket.on('move2',(from,to,piece)=>{
          const [fromrow,fromcol] = from
          const [torow,tocol] = to
          console.log(from,to,piece);
          boardlook[torow][tocol] =  piece
          boardlook[fromrow][fromcol] = ' '
         console.log(boardlook);
        })
    }

    return () => {
      allPices.forEach(pice => {
        pice.removeEventListener('dragstart', () => { });
        pice.removeEventListener('dragend', () => { });
      });

      chessSquares.forEach(square => {
        square.removeEventListener('dragover', () => { });
      });
    };
  }, [Piece, End, Start]);

  return (
    <div>
      <div id='chessboard' >

      </div>
    </div>
  );
};

export default App;
